define("AUTH_PATH", getPath("auth"));
define("CSS_PATH", getPath("css"));
define("JS_PATH", getPath("js"));
define("IMAGES_PATH", getPath("images"));
define("UPLOADS_PATH", getPath("uploads"));
define("CLASSES_PATH", getPath("classes"));
